import React, { useState } from 'react';
import { S3Client, PutObjectCommand } from '@aws-sdk/client-s3';
import './App.css';

const S3Uploader = () => {
    const [selectedFile, setSelectedFile] = useState(null);
    const [uploading, setUploading] = useState(false);
    const [uploadProgress, setUploadProgress] = useState(0);
    const [textInput, setTextInput] = useState('');
    const [apiResponse, setApiResponse] = useState(null); // State to store API response

    const handleFileChange = (e) => {
        setSelectedFile(e.target.files[0]);
    };

    const handleTextChange = (e) => {
        setTextInput(e.target.value);
    };

    const uploadFile = async () => {
        if (!selectedFile) return;

        setUploading(true);

        const s3 = new S3Client({
            region: 'us-west-1',
            credentials: {
                accessKeyId: 'AKIA4MTWJM2N7ZP6DB5M',
                secretAccessKey: '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'
            }
        });

        const params = {
            Bucket: 'pranav-file-upload-storage',
            Key: selectedFile.name,
            Body: selectedFile,
        };

        try {
            const command = new PutObjectCommand(params);
            const response = await s3.send(command);
            console.log('File uploaded successfully:', response);

            const apiUrl = 'https://8sewjemv47.execute-api.us-west-1.amazonaws.com/test/file';
            const apiParams = {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    input_text: textInput,
                    input_file_path: `pranav-file-upload-storage/${selectedFile.name}` // Include bucket name and filename in the S3 URL
                })
            };

            // Fetch API and store the response in state
            const responseFromApi = await fetch(apiUrl, apiParams);
            const responseData = await responseFromApi.json();
            setApiResponse(responseData);

        } catch (error) {
            console.error('Upload failed:', error);
        } finally {
            setUploading(false);
        }
    };

    return (
        // <div style={{ backgroundColor: 'black', color: 'white', textAlign: 'center'}}>
        //     <label>Text input: <input type='text' value={textInput} onChange={handleTextChange} /></label><br /><br />
        //     <input type="file" required onChange={handleFileChange} /><br /><br />
        //     <button onClick={uploadFile} disabled={uploading}>
        //         {uploading ? 'Uploading...' : 'Submit'}
        //     </button>
        //     {uploadProgress > 0 && <div>Progress: {uploadProgress}%</div>}
        //     {/* Display the API response */}
        //     {apiResponse && (
        //         <div>
        //             {/* <h2>API Response:</h2>
        //             <pre>{JSON.stringify(apiResponse, null, 2)}</pre> */}
        //             <p>File submitted successfully</p>
        //         </div>
        //     )}
        // </div>

        <div style={{ backgroundColor: 'black', color: 'white', textAlign: 'center', padding: '20px' }}>
            <label style={{ display: 'block', marginBottom: '10px' }}>Text input: <input type='text' value={textInput} onChange={handleTextChange} /></label>
            <br />
            <input type="file" required onChange={handleFileChange} style={{ marginBottom: '20px' }} />
            <br />
            <button onClick={uploadFile} disabled={uploading}>
                {uploading ? 'Uploading...' : 'Submit'}
            </button>
            {uploadProgress > 0 && <div style={{ marginTop: '20px' }}>Progress: {uploadProgress}%</div>}
            {/* Display the API response */}
            {apiResponse && (
                <div style={{ marginTop: '20px' }}>
                    {/* <h2>API Response:</h2>
                    <pre>{JSON.stringify(apiResponse, null, 2)}</pre> */}
                    <p>File submitted successfully</p>
                </div>
            )}
        </div>
    );
};

export default S3Uploader;
