import './App.css';
import React, { Component } from 'react';

class App extends Component {
  state = {
    selectedFile: null,
    fileUploadedSuccessfully: false,
  }

  render() {
    return (
      <form onSubmit={this.onFileUpload}>
        <label>
          Text input:
          <input type="text" />
        </label>
        <br />
        <label>
          File input:
          <input type="file" onChange={this.onFileChange} />
        </label>
        <br />
        <button type="submit">Submit</button>
      </form>
    )
  }
}

export default App;
